import React from 'react';
import { useParams } from 'react-router-dom';

function DepartmentPage() {
  const { building, department } = useParams();

  // S3 URL 설정
  const s3BaseUrl = "https://kumoh-intro-media.s3.amazonaws.com";
  const fileNames = {
    overview: `${building}-${department}-개요.jpg`,
    curriculum: `${building}-${department}-커리큘럼.jpg`,
    hallway: `${building}-${department}-복도.jpg`,
    classroom: `${building}-${department}-강의실.jpg`,
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>{building} - {department}</h1>
      <h2>학과 세부 정보</h2>
      <div>
        <h3>학과 개요</h3>
        <img src={`${s3BaseUrl}/${fileNames.overview}`} alt="학과 개요" style={{ width: '80%' }} />
      </div>
      <div>
        <h3>커리큘럼</h3>
        <img src={`${s3BaseUrl}/${fileNames.curriculum}`} alt="커리큘럼" style={{ width: '80%' }} />
      </div>
      <div>
        <h3>복도</h3>
        <img src={`${s3BaseUrl}/${fileNames.hallway}`} alt="복도" style={{ width: '80%' }} />
      </div>
      <div>
        <h3>강의실</h3>
        <img src={`${s3BaseUrl}/${fileNames.classroom}`} alt="강의실" style={{ width: '80%' }} />
      </div>
    </div>
  );
}

export default DepartmentPage;
