import React from 'react';
import { Link, useParams } from 'react-router-dom';

const departments = {
  'digital-building': ['컴퓨터공학과', '컴퓨터소프트웨어공학과', '전자공학과', '수리빅데이터공학과'],
  'techno-building': ['기계공학과', '산업공학과'],
  'global-building': ['영어영문학과', '경영학과'],
};

const buildingNames = {
  'digital-building': '디지털관',
  'techno-building': '테크노관',
  'global-building': '글로벌관',
};

function BuildingPage() {
  const { building } = useParams();

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>{buildingNames[building]}</h1> {/* 한글 이름 표시 */}
      {departments[building]?.map((department) => (
        <Link
          key={department}
          to={`/department/${department}`}
          style={{ display: 'block', margin: '20px', fontSize: '18px', textDecoration: 'none' }}
        >
          {department}
        </Link>
      ))}
    </div>
  );
}

export default BuildingPage; // Default export 추가
