import React from 'react';
import { useParams } from 'react-router-dom';

function DepartmentPage() {
  const { department } = useParams();

  // AWS S3에 업로드된 파일의 경로
  const fileName = "1floor.jpg"; // 파일 이름을 직접 입력
  const s3BaseUrl = "https://kumoh-intro-media.s3.ap-southeast-2.amazonaws.com"; // S3 버킷 URL
  
  const fileUrl = `${s3BaseUrl}/${fileName}`; // 파일의 전체 URL
  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>{department}</h1>
      {/* 업로드된 파일 표시 */}
      <img
        controls
        src={fileUrl} // S3에서 가져온 URL
        style={{ width: '80%', marginTop: '20px' }}
      />
    </div>
  );
}

export default DepartmentPage;
